patches = {'.debug': '--- qwt-sources/include/qwt_text.h.debug\tWed Nov 19 09:08:53 2003\n+++ qwt-sources/include/qwt_text.h\tWed Nov 19 09:08:53 2003\n@@ -37,6 +37,7 @@\n public:\n     virtual ~QwtText();\n \n+#ifndef PYQWT_DEBUG\n     static QwtText *makeText(const QString &, Qt::TextFormat,\n         int align, const QFont &, const QColor &color = QColor(),\n         const QPen &pen = QPen(Qt::NoPen),\n@@ -46,6 +47,17 @@\n         int align, const QFont &, const QColor &color = QColor(),\n         const QPen &pen = QPen(Qt::NoPen),\n         const QBrush &brush = QBrush(Qt::NoBrush));\n+#else\n+    static QwtText *makeText(const QString &, Qt::TextFormat,\n+        int align, const QFont &, const QColor &color = QColor(),\n+        const QPen &pen = QPen(Qt::red, 2),\n+        const QBrush &brush = QBrush(Qt::yellow));\n+\n+    static QwtText *makeText(const QString &, \n+        int align, const QFont &, const QColor &color = QColor(),\n+        const QPen &pen = QPen(Qt::red, 2),\n+        const QBrush &brush = QBrush(Qt::yellow));\n+#endif\n \n     static void setDefaultFormat(Qt::TextFormat);\n     static Qt::TextFormat defaultFormat();\n', '.autoscale': '--- qwt-sources/src/qwt_plot.cpp.autoscale\tFri Nov 21 06:49:42 2003\n+++ qwt-sources/src/qwt_plot.cpp\tFri Nov 21 07:33:42 2003\n@@ -9,6 +9,7 @@\n \n // vim: expandtab\n \n+#include <float.h>\n #include <qlabel.h>\n #include <qpainter.h>\n #include <qfocusdata.h>\n@@ -478,6 +479,47 @@\n         }\n     }\n \n+    // PyQwt adjust the autoscalers also for the markers\n+\n+    double min[axisCnt];\n+    double max[axisCnt];\n+    for (i = 0; i < axisCnt; i++)\n+    {\n+        min[i] = DBL_MAX;\n+        max[i] = DBL_MIN;\n+    }\n+    \n+    QwtPlotMarkerIterator itm = markerIterator();\n+    for (const QwtPlotMarker *m = itm.toFirst(); m != 0; m = ++itm)\n+    {\n+        const int xAxis = m->xAxis();\n+        const int yAxis = m->yAxis();\n+        const double x = m->xValue();\n+        const double y = m->yValue();\n+\n+        if (x<min[xAxis])\n+            min[xAxis] = x;\n+        if (x>max[xAxis])\n+            max[xAxis] = x;\n+        if (y<min[yAxis])\n+            min[yAxis] = y;\n+        if (x>max[yAxis])\n+            max[yAxis] = y;\n+    }\n+\n+    for (i = 0; i < axisCnt; i++)\n+    {\n+        if ( (min[i] != DBL_MAX) && (max[i] != DBL_MIN) )\n+        {\n+            if ( !resetDone[i] && (min[i] != max[i]) )\n+            {\n+                d_as[i].reset();\n+                resetDone[i] = TRUE;\n+            }\n+            d_as[i].adjust(min[i], max[i]);\n+        }\n+    }\n+    \n     //\n     // Adjust scales\n     //\n'}
